static void R_AddWorldSurface ( msurface_t * surf , int dlightBits ) { if ( surf -> viewCount == tr . viewCount ) { return ; } surf -> viewCount = tr . viewCount ; if ( R_CullSurface ( surf -> data , surf -> shader ) ) { return ; } if ( dlightBits ) { dlightBits = R_DlightSurface ( surf , dlightBits ) ; } R_AddDrawSurf ( surf -> data , surf -> shader , surf -> fogIndex , dlightBits ) ; } 