void Display ( glyph ) CharInfoRec * glyph ; { int h , w ; unsigned char * p ; int data ; int i ; p = ( unsigned char * ) glyph -> bits ; printf ( "Metrics: left=%d, right=%d, w=%d, above=%d, below=%d\n" , glyph -> metrics . leftSideBearing , glyph -> metrics . rightSideBearing , glyph -> metrics . characterWidth , glyph -> metrics . ascent , glyph -> metrics . descent ) ; for ( h = glyph -> metrics . ascent + glyph -> metrics . descent ; -- h >= 0 ; ) { w = glyph -> metrics . rightSideBearing - glyph -> metrics . leftSideBearing ; while ( w > 0 ) { data = * p ++ ; for ( i = 0 ; i < 8 ; i ++ ) { if ( data & 0x80 ) { printf ( "X" ) ; } else { printf ( "." ) ; } data <<= 1 ; } } printf ( "\n" ) ; } } 