void print_iface ( struct iface * iface ) { printf ( "interface %s {\n" , iface -> name ) ; if ( iface -> passive ) { printf ( "\tpassive\n" ) ; } printf ( "\tmetric %d\n" , iface -> metric ) ; printf ( "\tquery-interval %d\n" , iface -> query_interval ) ; printf ( "\tquery-response-interval %d\n" , iface -> query_resp_interval ) ; printf ( "\trobustness %d\n" , iface -> robustness ) ; printf ( "\tstartup-query-count %d\n" , iface -> startup_query_cnt ) ; printf ( "\tlast-member-query-count %d\n" , iface -> last_member_query_cnt ) ; printf ( "\tlast-member-query-interval %d\n" , iface -> last_member_query_interval ) ; printf ( "\tigmp-version %d\n" , iface -> igmp_version ) ; } 