static int nbd_handle_updated_info ( BlockDriverState * bs , Error * * errp ) { BDRVNBDState * s = ( BDRVNBDState * ) bs -> opaque ; int ret ; if ( s -> x_dirty_bitmap ) { if ( ! s -> info . base_allocation ) { error_setg ( errp , "requested x-dirty-bitmap %s not found" , s -> x_dirty_bitmap ) ; return - EINVAL ; } } if ( s -> info . flags & NBD_FLAG_READ_ONLY ) { ret = bdrv_apply_auto_read_only ( bs , "NBD export is read-only" , errp ) ; if ( ret < 0 ) { return ret ; } } if ( s -> info . flags & NBD_FLAG_SEND_FUA ) { bs -> supported_write_flags = BDRV_REQ_FUA ; bs -> supported_zero_flags |= BDRV_REQ_FUA ; } if ( s -> info . flags & NBD_FLAG_SEND_WRITE_ZEROES ) { bs -> supported_zero_flags |= BDRV_REQ_MAY_UNMAP ; if ( s -> info . flags & NBD_FLAG_SEND_FAST_ZERO ) { bs -> supported_zero_flags |= BDRV_REQ_NO_FALLBACK ; } } trace_nbd_client_handshake_success ( s -> export ) ; return 0 ; } 