static void to_gp11_param ( uint32_t pt , const TEE_Param params [ TEE_NUM_PARAMS ] , __GP11_TEE_Param gp11_params [ TEE_NUM_PARAMS ] ) { size_t n ; for ( n = 0 ; n < TEE_NUM_PARAMS ; n ++ ) { switch ( TEE_PARAM_TYPE_GET ( pt , n ) ) { case TEE_PARAM_TYPE_VALUE_INPUT : case TEE_PARAM_TYPE_VALUE_OUTPUT : case TEE_PARAM_TYPE_VALUE_INOUT : gp11_params [ n ] . value . a = params [ n ] . value . a ; gp11_params [ n ] . value . b = params [ n ] . value . b ; break ; case TEE_PARAM_TYPE_MEMREF_INPUT : case TEE_PARAM_TYPE_MEMREF_OUTPUT : case TEE_PARAM_TYPE_MEMREF_INOUT : gp11_params [ n ] . memref . buffer = params [ n ] . memref . buffer ; gp11_params [ n ] . memref . size = params [ n ] . memref . size ; break ; default : break ; } } } 