static void compound_results ( struct thread_data * res , struct Curl_addrinfo * ai ) { if ( res -> temp_ai && res -> temp_ai -> ai_family == PF_INET6 ) { struct Curl_addrinfo * temp_ai_tail = res -> temp_ai ; while ( temp_ai_tail -> ai_next ) { temp_ai_tail = temp_ai_tail -> ai_next ; } temp_ai_tail -> ai_next = ai ; } else { struct Curl_addrinfo * ai_tail = ai ; while ( ai_tail -> ai_next ) { ai_tail = ai_tail -> ai_next ; } ai_tail -> ai_next = res -> temp_ai ; res -> temp_ai = ai ; } } 