void gfs2_delete_debugfs_file ( struct gfs2_sbd * sdp ) { if ( sdp -> debugfs_dir ) { if ( sdp -> debugfs_dentry_glstats ) { debugfs_remove ( sdp -> debugfs_dentry_glstats ) ; sdp -> debugfs_dentry_glstats = NULL ; } if ( sdp -> debugfs_dentry_sbstats ) { debugfs_remove ( sdp -> debugfs_dentry_sbstats ) ; sdp -> debugfs_dentry_sbstats = NULL ; } debugfs_remove ( sdp -> debugfs_dir ) ; sdp -> debugfs_dir = NULL ; } } 