void ttfPrintLTSH ( FILE * fp , LTSHPtr ltsh ) { int i ; fprintf ( fp , "-------------------------------------\n" ) ; fprintf ( fp , "'LTSH' Version:\t %d\n" , ltsh -> version ) ; fprintf ( fp , "Number of Glyphs:\t %d\n" , ltsh -> numGlyphs ) ; fprintf ( fp , "\t Glyph # \t Threshold\n" ) ; for ( i = 0 ; i < ltsh -> numGlyphs ; i ++ ) { fprintf ( fp , "\t %d. \t\t %d\n" , i , ltsh -> yPels [ i ] ) ; } } 