static void signal_TERM ( int sig ) { DEBUG_MSG ( "Signal handler... (caught SIGNAL: %d) | %s" , sig , strsignal ( sig ) ) ; DEBUG_MSG ( "Signal handler... (caught SIGNAL: %d)" , sig ) ; ui_cleanup ( ) ; if ( sig == SIGINT ) { fprintf ( stderr , "\n\nUser requested a CTRL+C... (deprecated, next time use proper shutdown)\n\n" ) ; } else { fprintf ( stderr , "\n\n Shutting down %s (received SIGNAL: %d | %s)\n\n" , PROGRAM , sig , strsignal ( sig ) ) ; fprintf ( stderr , "\n\n Shutting down %s (received SIGNAL: %d)\n\n" , PROGRAM , sig ) ; } signal ( sig , SIG_IGN ) ; log_stop ( ) ; } 