void TsStartAddBaProcess ( struct rtllib_device * ieee , struct tx_ts_record * pTxTS ) { if ( pTxTS -> bAddBaReqInProgress == false ) { pTxTS -> bAddBaReqInProgress = true ; if ( pTxTS -> bAddBaReqDelayed ) { netdev_dbg ( ieee -> dev , "Start ADDBA after 60 sec!!\n" ) ; } else { netdev_dbg ( ieee -> dev , "Immediately Start ADDBA\n" ) ; mod_timer ( & pTxTS -> TsAddBaTimer , jiffies + 10 ) ; } } else { netdev_dbg ( ieee -> dev , "BA timer is already added\n" ) ; } } 