void malidp_mw_atomic_commit ( struct drm_device * drm , struct drm_atomic_state * old_state ) { struct malidp_drm * malidp = drm_to_malidp ( drm ) ; struct drm_writeback_connector * mw_conn = & malidp -> mw_connector ; struct drm_connector_state * conn_state = mw_conn -> base . state ; struct malidp_hw_device * hwdev = malidp -> dev ; struct malidp_mw_connector_state * mw_state ; if ( ! conn_state ) { return ; } mw_state = to_mw_state ( conn_state ) ; if ( conn_state -> writeback_job ) { struct drm_framebuffer * fb = conn_state -> writeback_job -> fb ; DRM_DEV_DEBUG_DRIVER ( drm -> dev , "Enable memwrite %ux%u:%d %pad fmt: %u\n" , fb -> width , fb -> height , mw_state -> pitches [ 0 ] , & mw_state -> addrs [ 0 ] , mw_state -> format ) ; drm_writeback_queue_job ( mw_conn , conn_state ) ; hwdev -> hw -> enable_memwrite ( hwdev , mw_state -> addrs , mw_state -> pitches , mw_state -> n_planes , fb -> width , fb -> height , mw_state -> format , ! mw_state -> rgb2yuv_initialized ?mw_state -> rgb2yuv_coeffs : NULL ) ; } else { DRM_DEV_DEBUG_DRIVER ( drm -> dev , "Disable memwrite\n" ) ; hwdev -> hw -> disable_memwrite ( hwdev ) ; } } 