void ubi_dump_vol_info ( const struct ubi_volume * vol ) { pr_err ( "Volume information dump:\n" ) ; pr_err ( "\tvol_id          %d\n" , vol -> vol_id ) ; pr_err ( "\treserved_pebs   %d\n" , vol -> reserved_pebs ) ; pr_err ( "\talignment       %d\n" , vol -> alignment ) ; pr_err ( "\tdata_pad        %d\n" , vol -> data_pad ) ; pr_err ( "\tvol_type        %d\n" , vol -> vol_type ) ; pr_err ( "\tname_len        %d\n" , vol -> name_len ) ; pr_err ( "\tusable_leb_size %d\n" , vol -> usable_leb_size ) ; pr_err ( "\tused_ebs        %d\n" , vol -> used_ebs ) ; pr_err ( "\tused_bytes      %lld\n" , vol -> used_bytes ) ; pr_err ( "\tlast_eb_bytes   %d\n" , vol -> last_eb_bytes ) ; pr_err ( "\tcorrupted       %d\n" , vol -> corrupted ) ; pr_err ( "\tupd_marker      %d\n" , vol -> upd_marker ) ; pr_err ( "\tskip_check      %d\n" , vol -> skip_check ) ; if ( vol -> name_len <= UBI_VOL_NAME_MAX && strnlen ( vol -> name , vol -> name_len + 1 ) == vol -> name_len ) { pr_err ( "\tname            %s\n" , vol -> name ) ; } else { } } 