void vx_set_internal_clock ( struct vx_core * chip , unsigned int freq ) { int clock ; clock = vx_calc_clock_from_freq ( chip , freq ) ; snd_printdd ( KERN_DEBUG "set internal clock to 0x%x from freq %d\n" , clock , freq ) ; if ( vx_is_pcmcia ( chip ) ) { vx_outb ( chip , HIFREQ , ( clock >> 8 ) & 0x0f ) ; vx_outb ( chip , LOFREQ , clock & 0xff ) ; } else { vx_outl ( chip , HIFREQ , ( clock >> 8 ) & 0x0f ) ; vx_outl ( chip , LOFREQ , clock & 0xff ) ; } mutex_unlock ( & chip -> lock ) ; } 