static void process_alerts ( spnego_gss_ctx_id_t ctx , struct negoex_message * messages , uint32_t nmessages ) { struct alert_message * msg ; struct negoex_auth_mech * mech ; msg = negoex_locate_alert_message ( messages , nmessages ) ; if ( msg != NULL && msg -> verify_no_key ) { mech = negoex_locate_auth_scheme ( ctx , msg -> scheme ) ; if ( mech != NULL ) { krb5_free_keyblock_contents ( NULL , & mech -> key ) ; krb5_free_keyblock_contents ( NULL , & mech -> verify_key ) ; } } } 