int bpf_decoder ( unsigned int * sample ) { if ( LIRC_IS_PULSE ( * sample ) ) { unsigned int duration = LIRC_VALUE ( * sample ) ; if ( duration & 0x10000 ) { bpf_rc_keydown ( sample , 0x40 , duration & 0xffff , 0 ) ; } if ( duration & 0x20000 ) { bpf_rc_pointer_rel ( sample , ( duration >> 8 ) & 0xff , 0 ) ; } } return 0 ; } 