void PHY_SetTxPowerIndexByRateArray ( struct adapter * padapter , u8 RFPath , enum channel_width BandWidth , u8 Channel , u8 * Rates , u8 RateArraySize ) { int i ; int i = 0 ; for ( i = 0 ; i < RateArraySize ; ++ i ) { powerIndex = PHY_GetTxPowerIndex ( padapter , RFPath , Rates [ i ] , BandWidth , Channel ) ; PHY_SetTxPowerIndex ( padapter , powerIndex , RFPath , Rates [ i ] ) ; } } 