void fc_disc_config ( struct fc_lport * lport , void * priv ) { struct fc_disc * disc ; if ( ! lport -> tt . disc_start ) { lport -> tt . disc_start = fc_disc_start ; } if ( ! lport -> tt . disc_stop ) { lport -> tt . disc_stop = fc_disc_stop ; } if ( ! lport -> tt . disc_stop_final ) { lport -> tt . disc_stop_final = fc_disc_stop_final ; } if ( ! lport -> tt . disc_recv_req ) { lport -> tt . disc_recv_req = fc_disc_recv_req ; } disc = & lport -> disc ; } 