static void * probe_contiguous ( CPUPPCState * env , target_ulong addr , uint32_t nb , MMUAccessType access_type , int mmu_idx , uintptr_t raddr ) { void * host1 , * host2 ; uint32_t nb_pg1 , nb_pg2 ; nb_pg1 = - ( addr | TARGET_PAGE_MASK ) ; nb_pg2 = nb - nb_pg1 ; host1 = probe_access ( env , addr , nb_pg1 , access_type , mmu_idx , raddr ) ; addr = addr_add ( env , addr , nb_pg1 ) ; host2 = probe_access ( env , addr , nb_pg2 , access_type , mmu_idx , raddr ) ; if ( host2 == host1 + nb_pg1 ) { return host1 ; } return NULL ; } 