extern bool slurm_option_set_by_cli ( slurm_opt_t * opt , int optval ) { int i ; if ( ! opt ) { debug3 ( "%s: opt=NULL optval=%u" , __func__ , optval ) ; return false ; } for ( i = 0 ; common_options [ i ] ; i ++ ) { if ( common_options [ i ] -> val == optval ) { break ; } } if ( ! common_options [ i ] ) { return false ; } return ( opt -> state [ i ] . set && ! opt -> state [ i ] . set_by_env ) ; } 