isc_result_t isc_thread_join ( isc_thread_t thread , isc_threadresult_t * rp ) { DWORD result ; DWORD threadrc ; result = WaitForSingleObject ( thread , INFINITE ) ; if ( result != WAIT_OBJECT_0 ) { return ( ISC_R_UNEXPECTED ) ; } if ( rp != NULL ) { if ( ! GetExitCodeThread ( thread , & threadrc ) ) { return ( ISC_R_UNEXPECTED ) ; } } ( void ) CloseHandle ( thread ) ; return ( ISC_R_SUCCESS ) ; } 