int test ( char * URL ) { rc = testone ( URL , 0 , 0 ) ; if ( rc ) { fprintf ( stderr , "test 0/0 failed: %d\n" , rc ) ; } rc = testone ( URL , 1 , 0 ) ; if ( ! rc ) { fprintf ( stderr , "test 1/0 failed: %d\n" , rc ) ; } rc = testone ( URL , 2 , 0 ) ; if ( ! rc ) { fprintf ( stderr , "test 2/0 failed: %d\n" , rc ) ; } rc = testone ( URL , 0 , 1 ) ; if ( ! rc ) { fprintf ( stderr , "test 0/1 failed: %d\n" , rc ) ; } rc = testone ( URL , 0 , 2 ) ; if ( ! rc ) { fprintf ( stderr , "test 0/2 failed: %d\n" , rc ) ; } return 0 ; } 