static int dmfe_open ( struct net_device * dev ) { struct dmfe_board_info * db = netdev_priv ( dev ) ; const int irq = db -> pdev -> irq ; int ret ; DMFE_DBUG ( 0 , "dmfe_open" , 0 ) ; ret = request_irq ( irq , dmfe_interrupt , IRQF_SHARED , dev -> name , dev ) ; if ( ret ) { return ret ; } db -> cr6_data = CR6_DEFAULT | dmfe_cr6_user_set ; db -> tx_packet_cnt = 0 ; db -> tx_queue_cnt = 0 ; db -> rx_avail_cnt = 0 ; db -> wait_reset = 0 ; db -> first_in_callback = 0 ; db -> NIC_capability = 0xf ; db -> PHY_reg4 = 0x1e0 ; if ( ! chkmode || ( db -> chip_id == PCI_DM9132_ID ) || ( db -> chip_revision >= 0x30 ) ) { db -> cr6_data |= DMFE_TXTH_256 ; db -> cr0_data = CR0_DEFAULT ; db -> dm910x_chk_mode = 4 ; } else { db -> cr6_data |= CR6_SFT ; db -> cr0_data = 0 ; db -> dm910x_chk_mode = 1 ; } dmfe_init_dm910x ( dev ) ; timer_setup ( & db -> timer , dmfe_timer , 0 ) ; db -> timer . expires = DMFE_TIMER_WUT + HZ * 2 ; add_timer ( & db -> timer ) ; return 0 ; } 