( usb_choose_configuration ) ; static int generic_probe ( struct usb_device * udev ) { int err , c ; if ( udev -> authorized == 0 ) { dev_err ( & udev -> dev , "Device is not authorized for usage\n" ) ; } else { c = usb_choose_configuration ( udev ) ; if ( c >= 0 ) { err = usb_set_configuration ( udev , c ) ; if ( err && err != - ENODEV ) { dev_err ( & udev -> dev , "can't set config #%d, error %d\n" , c , err ) ; } } } return 0 ; } 