u32 acpi_ev_sci_dispatch ( void ) { struct acpi_sci_handler_info * sci_handler ; acpi_cpu_flags flags ; ACPI_FUNCTION_NAME ( ev_sci_dispatch ) ; if ( ! acpi_gbl_sci_handler_list ) { return ( int_status ) ; } flags = acpi_os_acquire_lock ( acpi_gbl_gpe_lock ) ; sci_handler = acpi_gbl_sci_handler_list ; while ( sci_handler ) { int_status |= sci_handler -> address ( sci_handler -> context ) ; sci_handler = sci_handler -> next ; } acpi_os_release_lock ( acpi_gbl_gpe_lock , flags ) ; return ( int_status ) ; } 