( dm_audit_log_ti ) ; void dm_audit_log_bio ( const char * dm_msg_prefix , const char * op , struct bio * bio , sector_t sector , int result ) { struct audit_buffer * ab ; int dev_major = MAJOR ( bio -> bi_bdev -> bd_dev ) ; int dev_minor = MINOR ( bio -> bi_bdev -> bd_dev ) ; ab = dm_audit_log_start ( AUDIT_DM_EVENT , dm_msg_prefix , op ) ; audit_log_format ( ab , " dev=%d:%d sector=%llu res=%d" , dev_major , dev_minor , sector , result ) ; audit_log_end ( ab ) ; } 