acpi_status acpi_sysfs_table_handler ( u32 event , void * table , void * context ) { struct acpi_table_attr * table_attr ; switch ( event ) { case ACPI_TABLE_EVENT_INSTALL : table_attr = kzalloc ( sizeof ( acpi_table_attr ) , GFP_KERNEL ) ; if ( ! table_attr ) { return AE_NO_MEMORY ; } if ( acpi_table_attr_init ( dynamic_tables_kobj , table_attr , table ) ) { return AE_ERROR ; } list_add_tail ( & table_attr -> node , & acpi_table_attr_list ) ; break ; case ACPI_TABLE_EVENT_LOAD : case ACPI_TABLE_EVENT_UNLOAD : case ACPI_TABLE_EVENT_UNINSTALL : break ; default : return AE_BAD_PARAMETER ; } return AE_OK ; } 