static struct f_arg * mgt_f_read ( const char * fn ) { struct f_arg * fa ; char * f , * fnp ; ALLOC_OBJ ( fa , F_ARG_MAGIC ) ; AN ( fa ) ; REPLACE ( fa -> farg , fn ) ; VFIL_setpath ( & vcl_path , mgt_vcl_path ) ; if ( VFIL_searchpath ( vcl_path , NULL , & f , fn , & fnp ) || f == NULL ) { ARGV_ERR ( "Cannot read -f file '%s' (%s)\n" , fnp != NULL ?fnp : fn , VAS_errtxt ( errno ) ) ; } fa -> farg = fnp ; fa -> src = f ; return ( fa ) ; } 