void inplace_cb_delete ( struct module_env * env , enum inplace_cb_list_type type , int id ) { struct inplace_cb * temp = env -> inplace_cb_lists [ type ] ; struct inplace_cb * prev = NULL ; while ( temp ) { if ( temp -> id == id ) { if ( ! prev ) { env -> inplace_cb_lists [ type ] = temp -> next ; temp = env -> inplace_cb_lists [ type ] ; } else { prev -> next = temp -> next ; free ( temp ) ; temp = prev -> next ; } } else { prev = temp ; temp = temp -> next ; } } } 