static void secmap_file_check ( secmap_t * sm ) { if ( sm -> sm_ctf && ( secmap_content & CC_CONTENT_CTF ) != 0 && sm -> sm_nfound [ SECMAP_CTF ] == 0 ) { secmap_fail ( "expected object %s to have CTF, but it doesn't" , sm -> sm_obj ) ; } if ( sm -> sm_debug && ( secmap_content & CC_CONTENT_DEBUG ) != 0 && sm -> sm_nfound [ SECMAP_DEBUG ] == 0 ) { secmap_fail ( "expected object %s to have debug sections, " "but it doesn't" , sm -> sm_obj ) ; } } 