queue * enqueue ( queue * my_queue , void * my_node ) { node * new_node = ( node * ) my_node - 1 ; node * i = NULL ; node * j = my_queue -> front ; while ( j != NULL && * my_queue -> get_order ( new_node + 1 , j + 1 ) > 0 ) { i = j ; j = j -> node_next ; } if ( i == NULL ) { new_node -> node_next = my_queue -> front ; my_queue -> front = new_node ; } else { new_node -> node_next = i -> node_next ; i -> node_next = new_node ; } return my_queue ; } 