void sfl_poller_tick ( SFLPoller * poller ) { if ( poller -> countersCountdown == 0 ) { return ; } if ( poller -> sFlowCpReceiver == 0 ) { return ; } if ( -- poller -> countersCountdown == 0 ) { if ( poller -> getCountersFn != NULL ) { SFL_COUNTERS_SAMPLE_TYPE cs ; poller -> getCountersFn ( poller -> magic , poller , & cs ) ; } poller -> countersCountdown = poller -> sFlowCpInterval ; } } 