symbol_t * symtable_get ( char * name ) { symbol_t * stored_ptr ; DBT key ; DBT data ; int retval ; key . data = ( void * ) name ; key . size = strlen ( name ) ; if ( ( retval = symtable -> get ( symtable , & key , & data , 0 ) ) != 0 ) { if ( retval == - 1 ) { perror ( "Symbol table get operation failed" ) ; exit ( EX_SOFTWARE ) ; } if ( retval == 1 ) { symbol_t * new_symbol ; new_symbol = symbol_create ( name ) ; data . data = & new_symbol ; data . size = sizeof ( new_symbol ) ; return ( new_symbol ) ; } else { perror ( "Unexpected return value from db get routine" ) ; exit ( EX_SOFTWARE ) ; } } memcpy ( & stored_ptr , data . data , sizeof ( stored_ptr ) ) ; return ( stored_ptr ) ; } 