int skl_dsp_start_core ( struct sst_dsp * ctx , unsigned int core_mask ) { int ret ; ret = skl_dsp_core_unset_reset_state ( ctx , core_mask ) ; if ( ret < 0 ) { return ret ; } dev_dbg ( ctx -> dev , "unstall/run core: core_mask = %x\n" , core_mask ) ; if ( ! is_skl_dsp_core_enable ( ctx , core_mask ) ) { skl_dsp_reset_core ( ctx , core_mask ) ; dev_err ( ctx -> dev , "DSP start core failed: core_mask %x\n" , core_mask ) ; ret = - EIO ; } return ret ; } 