static const int gb_camera_configure_streams_validate_response ( struct gb_camera * gcam , struct gb_camera_configure_streams_response * resp , unsigned int nstreams ) { unsigned int i ; if ( resp -> padding [ 0 ] || resp -> padding [ 1 ] ) { gcam_err ( gcam , "response padding != 0\n" ) ; return - EIO ; } for ( i = 0 ; i < resp -> num_streams ; i ++ ) { struct gb_camera_stream_config_response * cfg = & resp -> config [ i ] ; if ( cfg -> padding ) { gcam_err ( gcam , "stream #%u padding != 0\n" , i ) ; return - EIO ; } } return 0 ; } 