static void intel_plane_uapi_info ( struct seq_file * m , struct intel_plane * plane ) { const struct intel_plane_state * plane_state = to_intel_plane_state ( plane -> base . state ) ; const struct drm_framebuffer * fb = plane_state -> uapi . fb ; struct drm_rect src , dst ; char rot_str [ 48 ] ; src = drm_plane_state_src ( & plane_state -> uapi ) ; dst = drm_plane_state_dest ( & plane_state -> uapi ) ; plane_rotation ( rot_str , sizeof ( rot_str ) , plane_state -> uapi . rotation ) ; seq_puts ( m , "\t\tuapi: [FB:" ) ; if ( fb ) { seq_printf ( m , "%d] %p4cc,0x%llx,%dx%d" , fb -> base . id , & fb -> format -> format , fb -> modifier , fb -> width , fb -> height ) ; } else { seq_puts ( m , "0] n/a,0x0,0x0," ) ; } seq_printf ( m , ", visible=%s, src=" DRM_RECT_FP_FMT ", dst=" DRM_RECT_FMT ", rotation=%s\n" , plane_visibility ( plane_state ) , DRM_RECT_FP_ARG ( & src ) , DRM_RECT_ARG ( & dst ) , rot_str ) ; } 