static void intel_panel_add_fixed_mode ( struct intel_connector * connector , struct drm_display_mode * fixed_mode , const char * type ) { struct drm_i915_private * i915 = to_i915 ( connector -> base . dev ) ; struct drm_display_info * info = & connector -> base . display_info ; fixed_mode -> type |= DRM_MODE_TYPE_PREFERRED | DRM_MODE_TYPE_DRIVER ; info -> width_mm = fixed_mode -> width_mm ; info -> height_mm = fixed_mode -> height_mm ; drm_dbg_kms ( & i915 -> drm , "[CONNECTOR:%d:%s] using %s fixed mode: " DRM_MODE_FMT "\n" , connector -> base . base . id , connector -> base . name , type , DRM_MODE_ARG ( fixed_mode ) ) ; list_add_tail ( & fixed_mode -> head , & connector -> panel . fixed_modes ) ; } 