static long load_acl_info ( int lib , int drv , long file_size , tlm_acls_t * acls , long * acl_spot , tlm_cmd_t * local_commands ) { char * bp ; int nread ; if ( * acl_spot == 0 ) { ( void ) memset ( acls , 0 , sizeof ( tlm_acls_t ) ) ; } bp = ( ( char * ) & acls -> acl_info ) + * acl_spot ; nread = input_mem ( lib , drv , local_commands , ( void * ) bp , file_size ) ; * acl_spot += nread ; acls -> acl_non_trivial = TRUE ; return ( file_size - nread ) ; } 