static bool HandleGlobalVar ( SymbolsInfo * info , VarDef * stmt ) { const char * elem , * field ; ExprDef * arrayNdx ; bool ret ; if ( elem && istreq ( elem , "key" ) ) { ret = SetSymbolsField ( info , & info -> default_key , field , arrayNdx , stmt -> value ) ; } if ( ! elem && ( istreq ( field , "name" ) || istreq ( field , "groupname" ) ) ) { ret = SetGroupName ( info , arrayNdx , stmt -> value ) ; } if ( ! elem && ( istreq ( field , "groupswrap" ) || istreq ( field , "wrapgroups" ) ) ) { log_err ( info -> ctx , "Global \"groupswrap\" not supported; Ignored\n" ) ; ret = true ; } if ( ! elem && ( istreq ( field , "groupsclamp" ) || istreq ( field , "clampgroups" ) ) ) { log_err ( info -> ctx , "Global \"groupsclamp\" not supported; Ignored\n" ) ; ret = true ; } if ( ! elem && ( istreq ( field , "groupsredirect" ) || istreq ( field , "redirectgroups" ) ) ) { log_err ( info -> ctx , "Global \"groupsredirect\" not supported; Ignored\n" ) ; ret = true ; } if ( ! elem && istreq ( field , "allownone" ) ) { log_err ( info -> ctx , "Radio groups not supported; " "Ignoring \"allownone\" specification\n" ) ; ret = true ; } else { ret = SetActionField ( info -> ctx , info -> actions , & info -> mods , elem , field , arrayNdx , stmt -> value ) ; } return ret ; } 