static void set_dcd_val_v1 ( struct imx_header * imxhdr , char * name , int lineno , int fld , uint32_t value , uint32_t off ) { dcd_v1_t * dcd_v1 = & imxhdr -> header . hdr_v1 . dcd_table ; switch ( fld ) { case CFG_REG_SIZE : dcd_v1 -> addr_data [ off ] . type = value ; break ; case CFG_REG_ADDRESS : dcd_v1 -> addr_data [ off ] . addr = value ; break ; case CFG_REG_VALUE : dcd_v1 -> addr_data [ off ] . value = value ; break ; default : break ; } } 