void AAS_ShowReachableAreas ( int areanum ) { aas_areasettings_t * settings ; static aas_reachability_t reach ; static int index , lastareanum ; static float lasttime ; if ( areanum != lastareanum ) { index = 0 ; lastareanum = areanum ; } settings = & ( * aasworld ) . areasettings [ areanum ] ; if ( index >= settings -> numreachableareas ) { index = 0 ; } if ( AAS_Time ( ) - lasttime > 1.5 ) { memcpy ( & reach , & ( * aasworld ) . reachability [ settings -> firstreachablearea + index ] , sizeof ( aas_reachability_t ) ) ; index ++ ; lasttime = AAS_Time ( ) ; AAS_PrintTravelType ( reach . traveltype ) ; botimport . Print ( PRT_MESSAGE , "(traveltime: %i)\n" , reach . traveltime ) ; } AAS_ShowReachability ( & reach ) ; } 