static void modestack_debugprint ( struct modestackdata * md ) { size_t i ; slog ( LG_DEBUG , "modestack_debugprint(): %s MODE %s" , md -> source , md -> channel -> name ) ; slog ( LG_DEBUG , "simple %x/%x" , md -> modes_on , md -> modes_off ) ; if ( md -> limitused ) { slog ( LG_DEBUG , "limit %u" , ( unsigned ) md -> limit ) ; } for ( i = 0 ; i < ignore_mode_list_size ; i ++ ) { if ( md -> extmodesused [ i ] ) { slog ( LG_DEBUG , "ext %d %s" , ( int ) i , md -> extmodes [ i ] ) ; } } slog ( LG_DEBUG , "pmodes %s%s" , md -> pmodes , md -> params ) ; slog ( LG_DEBUG , "totallen %d/%d" , md -> totalparamslen , md -> totallen ) ; } 