static void m_uid ( struct sourceinfo * si , int parc , char * parv [ ] ) { struct server * s ; struct user * u ; if ( parc == 9 ) { s = si -> s ; slog ( LG_DEBUG , "m_uid(): new user on `%s': %s" , s -> name , parv [ 0 ] ) ; u = user_add ( parv [ 0 ] , parv [ 4 ] , parv [ 5 ] , NULL , parv [ 6 ] , parv [ 7 ] , parv [ 8 ] , s , atoi ( parv [ 2 ] ) ) ; if ( u == NULL ) { return ; } user_mode ( u , parv [ 3 ] ) ; if ( strchr ( parv [ 3 ] , 'S' ) ) { u -> flags |= UF_IMMUNE ; } if ( s -> flags & SF_EOB ) { slog ( LG_DEBUG , "m_uid():   parv[%d] = %s" , i , 0 ) ; } } else { int i ; slog ( LG_DEBUG , "m_uid(): got UID with wrong number of params" ) ; for ( i = 0 ; i < parc ; i ++ ) { slog ( LG_DEBUG , "m_uid():   parv[%d] = %s" , i , parv [ i ] ) ; } } } 