void xen_smp_intr_free_pv ( unsigned int cpu ) { kfree ( per_cpu ( xen_irq_work , cpu ) . name ) ; per_cpu ( xen_irq_work , cpu ) . name = NULL ; kfree ( per_cpu ( xen_pmu_irq , cpu ) . name ) ; per_cpu ( xen_pmu_irq , cpu ) . name = NULL ; if ( per_cpu ( xen_pmu_irq , cpu ) . irq >= 0 ) { unbind_from_irqhandler ( per_cpu ( xen_pmu_irq , cpu ) . irq , NULL ) ; per_cpu ( xen_pmu_irq , cpu ) . irq = - 1 ; } } 