static bool variable_defined_or_used_in_range ( zend_ssa * ssa , int var , int start , int end ) { while ( start < end ) { if ( ( ssa_op -> op1_def >= 0 && ssa -> vars [ ssa_op -> op1_def ] . var == var ) || ( ssa_op -> op2_def >= 0 && ssa -> vars [ ssa_op -> op2_def ] . var == var ) || ( ssa_op -> result_def >= 0 && ssa -> vars [ ssa_op -> result_def ] . var == var ) || ( ssa_op -> op1_use >= 0 && ssa -> vars [ ssa_op -> op1_use ] . var == var ) || ( ssa_op -> op2_use >= 0 && ssa -> vars [ ssa_op -> op2_use ] . var == var ) || ( ssa_op -> result_use >= 0 && ssa -> vars [ ssa_op -> result_use ] . var == var ) ) { return 1 ; } start ++ ; } return 0 ; } 