void BotDumpSynonymList ( bot_synonymlist_t * synlist ) { FILE * fp ; bot_synonymlist_t * syn ; bot_synonym_t * synonym ; fp = Log_FilePointer ( ) ; for ( syn = synlist ; syn ; syn = syn -> next ) { fprintf ( fp , "%ld : [" , syn -> context ) ; for ( synonym = syn -> firstsynonym ; synonym ; synonym = synonym -> next ) { fprintf ( fp , "(\"%s\", %1.2f)" , synonym -> string , synonym -> weight ) ; if ( synonym -> next ) { fprintf ( fp , ", " ) ; } } fprintf ( fp , "]\n" ) ; } } 