static void show_http_proxy_options ( const struct http_proxy_options * o ) { int i ; msg ( D_SHOW_PARMS , "BEGIN http_proxy" ) ; SHOW_STR ( server ) ; SHOW_STR ( port ) ; SHOW_STR ( auth_method_string ) ; SHOW_STR ( http_version ) ; SHOW_STR ( user_agent ) ; for ( i = 0 ; i < MAX_CUSTOM_HTTP_HEADER && o -> custom_headers [ i ] . name ; i ++ ) { if ( o -> custom_headers [ i ] . content ) { msg ( D_SHOW_PARMS , "  custom_header[%d] = %s: %s" , i , o -> custom_headers [ i ] . name , o -> custom_headers [ i ] . content ) ; } else { msg ( D_SHOW_PARMS , "  custom_header[%d] = %s" , i , o -> custom_headers [ i ] . name ) ; } } msg ( D_SHOW_PARMS , "END http_proxy" ) ; } 