static void encx24j600_dump_rsv ( struct encx24j600_priv * priv , const char * msg , struct rsv * rsv ) { struct net_device * dev = priv -> ndev ; netdev_info ( dev , "RX packet Len:%d\n" , rsv -> len ) ; netdev_dbg ( dev , "%s - NextPk: 0x%04x\n" , msg , rsv -> next_packet ) ; netdev_dbg ( dev , "RxOK: %d, DribbleNibble: %d\n" , RSV_GETBIT ( rsv -> rxstat , RSV_RXOK ) , RSV_GETBIT ( rsv -> rxstat , RSV_DRIBBLENIBBLE ) ) ; netdev_dbg ( dev , "CRCErr:%d, LenChkErr: %d, LenOutOfRange: %d\n" , RSV_GETBIT ( rsv -> rxstat , RSV_CRCERROR ) , RSV_GETBIT ( rsv -> rxstat , RSV_LENCHECKERR ) , RSV_GETBIT ( rsv -> rxstat , RSV_LENOUTOFRANGE ) ) ; netdev_dbg ( dev , "Multicast: %d, Broadcast: %d, LongDropEvent: %d, CarrierEvent: %d\n" , RSV_GETBIT ( rsv -> rxstat , RSV_RXMULTICAST ) , RSV_GETBIT ( rsv -> rxstat , RSV_RXBROADCAST ) , RSV_GETBIT ( rsv -> rxstat , RSV_RXLONGEVDROPEV ) , RSV_GETBIT ( rsv -> rxstat , RSV_CARRIEREV ) ) ; } 