const struct ahc_pci_identity * ahc_find_pci_device ( ahc_dev_softc_t pci ) { uint64_t full_id ; uint16_t device ; uint16_t vendor ; uint16_t subdevice ; uint16_t subvendor ; const struct ahc_pci_identity * entry ; u_int i ; vendor = ahc_pci_read_config ( pci , PCIR_DEVVENDOR , 2 ) ; device = ahc_pci_read_config ( pci , PCIR_DEVICE , 2 ) ; subvendor = ahc_pci_read_config ( pci , PCIR_SUBVEND_0 , 2 ) ; subdevice = ahc_pci_read_config ( pci , PCIR_SUBDEV_0 , 2 ) ; full_id = ahc_compose_id ( device , vendor , subdevice , subvendor ) ; for ( i = 0 ; i < ahc_num_pci_devs ; i ++ ) { entry = & ahc_pci_ident_table [ i ] ; if ( entry -> full_id == ( full_id & entry -> id_mask ) ) { if ( entry -> name == NULL ) { return ( NULL ) ; } return ( entry ) ; } } return ( NULL ) ; } 