int ipmi_sdr_iter ( ipmi_handle_t * ihp , int * func ( ipmi_handle_t * , const char * , ipmi_sdr_t * , void * ) , void * data ) { ipmi_sdr_cache_ent_t * ent ; int ret ; for ( ent = ipmi_hash_first ( ihp -> ih_sdr_cache ) ; ent != NULL ; ent = ipmi_hash_next ( ihp -> ih_sdr_cache , ent ) ) { if ( ( ret = func ( ihp , ent -> isc_name , ent -> isc_sdr , data ) ) != 0 ) { return ( ret ) ; } } return ( 0 ) ; } 