extern void spawn_subcmd_free ( spawn_subcmd_t * subcmd ) { int i ; if ( subcmd ) { xfree ( subcmd -> cmd ) ; if ( subcmd -> argv ) { for ( i = 0 ; i < subcmd -> argc ; i ++ ) { xfree ( subcmd -> argv [ i ] ) ; } xfree ( subcmd -> argv ) ; } if ( subcmd -> info_keys ) { for ( i = 0 ; i < subcmd -> info_cnt ; i ++ ) { xfree ( subcmd -> info_keys [ i ] ) ; } xfree ( subcmd -> info_keys ) ; } if ( subcmd -> info_vals ) { for ( i = 0 ; i < subcmd -> info_cnt ; i ++ ) { xfree ( subcmd -> info_vals [ i ] ) ; } xfree ( subcmd -> info_vals ) ; } } } 